# !/usr/bin/env python# -*- coding: utf-8 -*-import xadminfrom xadmin import viewsfrom .models import Category, Point, Article# Register your models here.class BaseSetting(object):    enable_themes = True    use_bootswatch = Trueclass GlobalSettings(object):    site_title = "后台管理系统"    site_footer = "Demo"    # menu_style = "accordion"class CategoryAdmin(object):    passclass PointAdmin(object):    passclass ArticleAdmin(object):    style_fields = {"content": "ueditor"}xadmin.site.register(views.BaseAdminView, BaseSetting)xadmin.site.register(views.CommAdminView, GlobalSettings)xadmin.site.register(Category, CategoryAdmin)xadmin.site.register(Point, PointAdmin)xadmin.site.register(Article, ArticleAdmin)